import React from 'react';
import './send.css';
class Send extends React.Component
{
	constructor(props) 
	{
		super(props);
		this.state = 
		{
			client: null
		};
	}
	componentDidMount()
	{
		this.setState({client: this.props.client });
		console.log(this.state);
	}
	send()
	{
		const docData = 
		{
			type: "sendData",
			message: document.getElementById("sendMsg").value,
			date: new Date(),
			name: "ripple",
			color: "red"
		}
		this.state.client.send(JSON.stringify(docData));
	}
	render()
	{
		console.log(this.props.client);
		return <div id = "sendDiv">
			<input type = "text" id = "sendMsg"></input>
			<button id = "send" onClick={this.send}><i className = "fa fa-paper-plane"></i></button>
		</div>
	}

}

export default Send;
