import React from 'react';
import './login.css';
import logo from './logo1.svg';
class Login extends React.Component
{
	login()
	{
		var u = document.getElementById('u');
		var p = document.getElementById('p');
		var info = 
		{
			"username" : u.value,
			"password" : p.value
		};
		console.log(info);
	}
	handleSubmit = (event) => 
	{
		fetch('http://localhost:5000/express_backend', 
		{
			method: 'POST',
			// We convert the React state to JSON and send it as the POST body
			body: JSON.stringify(
			{
				"username" : "ripple",
				"password" : "idk"
			}),
			headers: 
			{
				'Accept': 'application/json',
				'Content-Type': 'application/json'
			}
		})
		.then(function(response) {
        console.log(response)
        return response.json();
      });
		event.preventDefault();
	}
	render()
	{
		return <div>
		        <img src={logo} className = "App-logo" alt = "logo" />
				<div id = "title">Nuclei</div>
				<div id = "login"> 
					<input type = "text" id = "u" placeholder = "Username..."></input>
					<input type = "text" id = "p" placeholder = "Password..."></input>
				</div>
				<button id = "loginButton" onClick = {this.login}>Login</button>
				<div id = "line">or</div>
				<button id = "createAccount" onClick = {this.handleSubmit}>Create New Account</button>
		</div>
	}
}

export default Login;
